import React, { useState, useContext, useEffect } from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import SearchBar from './components/Searchbar';
import RecipeList from './components/recipelist';
import RecipeDetail from './components/recipedetail';
import Register from './components/Register';
import Login from './components/Login';
import ProfileButton from './components/ProfileButton';
import { AuthContext } from './context/AuthContext';
import './App.css';

function App() {
  const [recipes, setRecipes] = useState([]);
  const { authToken, setAuthToken } = useContext(AuthContext);

  const handleSearch = (query) => {
    fetch(`/api/search?query=${encodeURIComponent(query)}`, {
      headers: {
        Authorization: `Bearer ${authToken}`,
      },
    })
      .then((res) => res.json())
      .then((data) => setRecipes(data))
      .catch(() => setRecipes([]));
  };

  return (
    <div>
      {authToken && (
        <div className="profile-button-wrapper">
          <ProfileButton
            authToken={authToken}
            onLogout={() => setAuthToken(null)}
            onNavigateToSavedRecipes={() => {}}
          />
        </div>
      )}
      <div className="welcome-banner">
        Welcome to Recipe Finder!
        <p className="welcome-tagline">Discover recipes for every craving.</p>
      </div>
      <div className="search-section">
        <SearchBar onSearch={handleSearch} />
        <div className="search-filters">
          <button className="filter-button">Vegetarian</button>
          <button className="filter-button">Desserts</button>
          <button className="filter-button">Quick Meals</button>
        </div>
      </div>
      {recipes.length > 0 ? (
        <RecipeList recipes={recipes} />
      ) : (
        <div className="empty-state">
          <p>No results yet! Try searching for something delicious.</p>
          <img src="/assets/placeholder.png" alt="Empty State" />
        </div>
      )}
    </div>
  );
}

export default App;
