// src/App.js
import React, { useState, useEffect, useContext } from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import SearchBar from './components/Searchbar';
import RecipeList from './components/recipelist';
import RecipeDetail from './components/recipedetail';
import Register from './components/Register';
import Login from './components/Login';
import { AuthContext } from './context/AuthContext';
import ProfileButton from './components/ProfileButton';
import './App.css';

function App() {
  const [recipes, setRecipes] = useState([]);
  const { authToken, setAuthToken } = useContext(AuthContext);

  const handleSearch = (query) => {
    fetch(`/api/search?query=${encodeURIComponent(query)}`, {
      headers: {
        Authorization: `Bearer ${authToken}`,
      },
    })
      .then((res) => {
        if (res.status === 401 || res.status === 403) {
          setAuthToken(null);
          throw new Error('Unauthorized');
        }
        if (!res.ok) throw new Error('Search failed');
        return res.json();
      })
      .then((data) => setRecipes(data))
      .catch((err) => {
        console.error('Error:', err);
        setRecipes([]); // Clear recipes on error
      });
  };

  const handleLogout = () => {
    setAuthToken(null);
  };

  const handleNavigateToSavedRecipes = () => {
    fetch('/api/recipes', {
      headers: {
        Authorization: `Bearer ${authToken}`,
      },
    }) // Make a GET request to the /api/recipes route
      .then((response) => {
        if (!response.ok) {
          throw new Error('Failed to fetch saved recipes');
        }
        return response.json(); // Parse the response as JSON
      })
      .then((data) => {
        setRecipes(data); // Update the recipes state with the fetched data
      })
      .catch((error) => {
        console.error('Error fetching saved recipes:', error);
      });
  };

  return (
    <div className="app-container">
      {authToken && (
        <div className="profile-button-wrapper">
          <ProfileButton
            authToken={authToken}
            onLogout={handleLogout}
            onNavigateToSavedRecipes={handleNavigateToSavedRecipes}
          />
        </div>
      )}

      <header className="welcome-banner">
        <h1 className="welcome-title">Welcome to Recipe Finder!</h1>
        <p className="welcome-tagline">Discover recipes for every craving.</p>
      </header>

      <div className="search-section">
        <SearchBar onSearch={handleSearch} />
        <div className="search-filters">
          <button className="filter-button" onClick={() => handleSearch('Vegetarian')}>
            Vegetarian
          </button>
          <button className="filter-button" onClick={() => handleSearch('Desserts')}>
            Desserts
          </button>
          <button className="filter-button" onClick={() => handleSearch('Quick Meals')}>
            Quick Meals
          </button>
        </div>
      </div>

      <main>
        {recipes.length > 0 ? (
          <RecipeList recipes={recipes} />
        ) : (
          <div className="empty-state">
            <p className="empty-message">
              No results yet! Try searching for something delicious.
            </p>
            <img
              className="empty-image"
              src="/assets/placeholder.png"
              alt="No recipes found"
            />
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
